package com.yiyang.demo.service.impl;

import com.alibaba.fastjson.JSON;
import com.alibaba.fastjson.JSONObject;
import com.github.pagehelper.PageInfo;
import com.google.common.collect.Lists;
import com.yiyang.demo.es.EsSearchAfterHandler;
import com.yiyang.demo.model.DocBean;
import com.yiyang.demo.service.IElasticService;
import com.yiyang.demo.utils.ObjectToMapUtils;
import lombok.extern.slf4j.Slf4j;
import org.apache.commons.collections.CollectionUtils;
import org.apache.commons.lang3.StringUtils;
import org.apache.lucene.search.TotalHits;
import org.elasticsearch.action.bulk.BulkRequest;
import org.elasticsearch.action.bulk.BulkResponse;
import org.elasticsearch.action.index.IndexRequest;
import org.elasticsearch.action.index.IndexResponse;
import org.elasticsearch.action.search.SearchRequest;
import org.elasticsearch.action.search.SearchResponse;
import org.elasticsearch.client.RequestOptions;
import org.elasticsearch.client.RestHighLevelClient;
import org.elasticsearch.common.settings.Settings;
import org.elasticsearch.common.unit.TimeValue;
import org.elasticsearch.common.xcontent.NamedXContentRegistry;
import org.elasticsearch.common.xcontent.XContentFactory;
import org.elasticsearch.common.xcontent.XContentParser;
import org.elasticsearch.common.xcontent.XContentType;
import org.elasticsearch.rest.RestStatus;
import org.elasticsearch.search.SearchHit;
import org.elasticsearch.search.SearchHits;
import org.elasticsearch.search.SearchModule;
import org.elasticsearch.search.builder.SearchSourceBuilder;
import org.elasticsearch.search.sort.SortOrder;
import org.springframework.stereotype.Service;

import javax.annotation.Resource;
import java.io.IOException;
import java.util.ArrayList;
import java.util.Arrays;
import java.util.Collections;
import java.util.List;
import java.util.concurrent.TimeUnit;

@Service
@Slf4j
public class IElasticServiceImpl implements IElasticService {


    @Resource
    private RestHighLevelClient client;

    private static final String NBA_INDEX = "record_traffic-2021-02-20";

    @Resource
    private EsSearchAfterHandler esSearchAfterHandler;

    /**
     * 新增操作
     * @param docBean
     * @throws IOException
     */
    @Override
    public void save(DocBean docBean) throws IOException {
        docBean.setContent("");
        IndexRequest request = new IndexRequest(NBA_INDEX).id(String.valueOf(docBean.getId())).source(ObjectToMapUtils.beanToMap(docBean));
        IndexResponse response = client.index(request, RequestOptions.DEFAULT);
        System.out.println(JSONObject.toJSON(response));
    }


    /**
     * 查询封装，带分页。使用from + size的做法，超过10000条数据就会报错
     * 适用于数量小的
     * 缺点：
     *  1. from+size在深度分页时，会带来严重的性能问题：
     *  2. CPU、内存、IO、网络带宽
     *  3. 数据量越大，越往后翻页，性能越低
     * @param searchSourceBuilder
     * @param pageNum
     * @param pageSize
     * @return
     * @throws IOException
     */
    public PageInfo<JSONObject> search(SearchSourceBuilder searchSourceBuilder, int pageNum, int pageSize) throws Exception {
        SearchRequest searchRequest = new SearchRequest(NBA_INDEX);
        searchSourceBuilder.trackTotalHits(true);
        searchSourceBuilder.fetchSource(null ,new String[] {"content"});
        searchSourceBuilder.size(pageSize);
        searchSourceBuilder.from(pageNum);
        searchRequest.source(searchSourceBuilder);
        SearchResponse searchResponse = client.search(searchRequest, RequestOptions.DEFAULT);
        SearchHits hits = searchResponse.getHits();
        List<JSONObject> list = new ArrayList<>();
        for (SearchHit hit : hits) {
            String sourceAsString = hit.getSourceAsString();
            JSONObject jsonObject = JSON.parseObject(sourceAsString);
            list.add(jsonObject);
        }
        int total = (int)hits.getTotalHits().value;
        log.info("返回总数为：" + total);

        // 封装分页
        PageInfo<JSONObject> page = new PageInfo<>();
        page.setList(list);
        page.setPageNum(pageNum);
        page.setPageSize(pageSize);
        page.setTotal(total);
        page.setPages(total== 0 ? 0: (total%pageSize == 0 ? total / pageSize : (total / pageSize) + 1));
        page.setHasNextPage(page.getPageNum() < page.getPages());
        return page;
    }

    /**
     * 查询封装，search after
     * 缺点：不适合做实时查询
     * @param sourceBuilder
     * @param pageNum
     * @param pageSize
     * @return
     * @throws IOException
     */
    public PageInfo<JSONObject> searchAfter(SearchSourceBuilder sourceBuilder, int pageNum, int pageSize) throws Exception {
        SearchRequest searchRequest = new SearchRequest(NBA_INDEX);
        sourceBuilder.trackTotalHits(true);
        sourceBuilder.fetchSource(null ,new String[] {"content"});
        sourceBuilder.size(pageSize);
        sourceBuilder.sort("id", SortOrder.ASC);
        searchRequest.source(sourceBuilder);
        SearchResponse searchResponse = client.search(searchRequest, RequestOptions.DEFAULT);
        TotalHits totalHits = searchResponse.getHits().getTotalHits();
        int total = (int)totalHits.value;
        log.info("返回总数为：" + total);
        // 计算循环的次数
        int count = total / pageSize + 1;
        if (pageNum > count) {
            pageNum = count;
        } else {
            count = pageNum;
        }
        SearchHit[] searchHits = searchResponse.getHits().getHits();
        List<JSONObject> list = new ArrayList<>();
        while (count > 0) {
            if (count <= 1) {
                for (SearchHit hit : searchHits) {
                    String sourceAsString = hit.getSourceAsString();
                    JSONObject jsonObject = JSON.parseObject(sourceAsString);
                    list.add(jsonObject);
                }
                break;
            } else {
                // 使用searchAfter循环
                SearchHit last = searchHits[searchHits.length - 1];
                System.out.println(Arrays.asList(last.getSortValues()));
                sourceBuilder.searchAfter(last.getSortValues());
                searchResponse = client.search(searchRequest, RequestOptions.DEFAULT);
                searchHits = searchResponse.getHits().getHits();
            }
            count--;
        }
        // 封装分页
        PageInfo<JSONObject> page = new PageInfo<>();
        page.setList(list);
        page.setPageNum(pageNum);
        page.setPageSize(pageSize);
        page.setTotal(total);
        page.setPages(total== 0 ? 0: (total%pageSize == 0 ? total / pageSize : (total / pageSize) + 1));
        page.setHasNextPage(page.getPageNum() < page.getPages());
        return page;
    }

    @Override
    public PageInfo<JSONObject> searchAfter(String query, String searchAfterId, int pageSize) throws Exception {
        SearchRequest searchRequest = new SearchRequest("record_traffic_test");
        SearchSourceBuilder sourceBuilder = jsonToSearchSourceBuilder(query);
        sourceBuilder.trackTotalHits(true);
        sourceBuilder.size(pageSize);
        sourceBuilder.sort("_id", SortOrder.ASC);
        sourceBuilder.fetchSource(null, new String[]{"wrapper_traffic"});
        searchRequest.source(sourceBuilder);

        SearchResponse searchResponse;

        SearchHit[] searchHits = null;
        int total = 0;
        int count = 0;
        Object[] sortValues = null;
        try {
            searchResponse = client.search(searchRequest, RequestOptions.DEFAULT);
            TotalHits totalHits = searchResponse.getHits().getTotalHits();
            total = (int) totalHits.value;
            log.info("search total：{}", total);
            searchHits = searchResponse.getHits().getHits();
            SearchHit last = searchHits[searchHits.length - 1];
            sortValues = last.getSortValues();
            for (SearchHit hit : searchHits) {
                log.info("查询出来的_id: {}", hit.getId());
                String sourceAsString = hit.getSourceAsString();
                JSONObject jsonObject = JSON.parseObject(sourceAsString);
                log.info("查询出来的数据: {}", jsonObject);
                count++;
            }
            log.info("searchAfter is：{}", Arrays.toString(sortValues));
        } catch (Exception e) {
            log.info(e.getMessage());
        }

        if (total == 0) {
            return null;
        }

        try {
            while (true) {
                // 使用searchAfter循环
                log.info("recycle searchAfter is：{}", Arrays.toString(sortValues));
                sourceBuilder.searchAfter(sortValues);
                searchResponse = client.search(searchRequest, RequestOptions.DEFAULT);
                searchHits = searchResponse.getHits().getHits();
                for (SearchHit hit : searchHits) {
                    String sourceAsString = hit.getSourceAsString();
                    JSONObject jsonObject = JSON.parseObject(sourceAsString);
                    log.info("查询出来的数据: {}", jsonObject);
                    count++;
                }

                if (searchHits.length == 0) {
                    break;
                }

                SearchHit last = searchHits[searchHits.length - 1];
                sortValues = last.getSortValues();
            }
        } catch (IOException e) {
            log.info(e.getMessage());
        }

        log.info("总共查询出来的数量是：{}", count);

        int pageNum = 1;

        // 封装分页
        PageInfo<JSONObject> page = new PageInfo<>();
        page.setList(Lists.newArrayList());
        page.setPageNum(pageNum);
        page.setPageSize(pageSize);
        page.setTotal(total);
        page.setPages(total == 0 ? 0 : (total % pageSize == 0 ? total / pageSize : (total / pageSize) + 1));
        page.setHasNextPage(page.getPageNum() < page.getPages());
        return page;
    }

    private SearchSourceBuilder jsonToSearchSourceBuilder(String query) {
        SearchSourceBuilder searchSourceBuilder = new SearchSourceBuilder();
        log.info("search condition is : {}", query);
        if (StringUtils.isNotBlank(query)) {
            SearchModule searchModule = new SearchModule(Settings.EMPTY, false, Collections.emptyList());
            try (XContentParser parser = XContentFactory.xContent(XContentType.JSON).createParser(new NamedXContentRegistry(searchModule.getNamedXContents()), null, query)) {
                searchSourceBuilder.parseXContent(parser);
            } catch (IOException e) {
                log.error(e.getMessage());
            }
        }
        return searchSourceBuilder;
    }

    @Override
    public void batchSave(List<DocBean> list) throws IOException {
        if (CollectionUtils.isEmpty(list)) {
            return;
        }
        BulkRequest bulkRequest = new BulkRequest ();
        bulkRequest.timeout(new TimeValue(60, TimeUnit.SECONDS));

        list.stream().forEach(item -> {
            item.setContent("");
            bulkRequest.add(new IndexRequest("batch_record_traffic").id(String.valueOf(item.getId())).source(ObjectToMapUtils.beanToMap(item)));
        });
        //4.执行请求
        BulkResponse bulkResponse = client.bulk (bulkRequest, RequestOptions.DEFAULT);
        //5.响应 判断是否执行成功
        RestStatus status = bulkResponse.status ();
        log.info("批量添加返回状态：{}", status);
    }
}
